ruleToc.nextTocUrl:
@js:
var list=[];
if(n=result.match(/totalPage":(\d+)/)){
n=result.match(/totalPage":(\d+)/)[1];
for(var i=2;i<=n;i++){
list.push(baseUrl.replace(/page=1/,'page='+i))
}}
list

ruleToc.chapterList:
@js:
let v = [],
		list = [];
JSON.parse(result).list.map($=>{

//分卷判定
		V = $.name;
		if(v[v.length-1]!=V&&!/^\s*$/.test(V)){
				v.push(V)
				list.push({
						name: '📖['+V+']📖',
						volume: true
					})
			}

$.bookChapters.map($=>{
		return list.push({
				name: $.name,
				url: `https://readbook-service-freebook.cread.com/cx/itf/chapterRead?bookId=${$.bookid}&chapterId=${$.id}`,
				info: `章节字数：${$.wordCount}　更新时间：${$.updateDate}`
			});
	});
});
v.length<2?list.filter($=>!$.volume):list

ruleToc.chapterName:
@js:
result=new String(result);
if(!(result.match(/最新/)))
result=(result.match(/free/)?'':'🔒')+(result.replace(/[\s\S]*title.*>(.+)<\/a>[\s\S]*/,'$1'));
else result='';

ruleToc.nextTocUrl:
@js:
var n=result.match(/[^\d](\d+)页/);
n=(n)?n[1]:'1';
var list=[];
for(var i=2;i<=n;i++){list.push(baseUrl.replace(/page=\d+/,'page=')+i);}
list;
