id.__NEXT_DATA__@html<js>
var urls = [];
if (result) {
    var s = /<script[\s\S]*>([\s\S]*)<\/script>/.exec(result)[1];
    s = JSON.parse(s);
    if (s.props.pageProps.chapterAPIPath) {
        var url = "http://rouman5.com" + s.props.pageProps.chapterAPIPath;

        var v = java.ajax(url);
        var p = JSON.parse(v);
        urls = p.chapter.images.map(v => v.src)

    } else if (s.props.pageProps.images) {
        urls = s.props.pageProps.images.map(v => v.src)
    }
}
urls.map(v => `<img src="${v}" />`).join('\n')
</js>

