ruleContent.content:
.comic@img@html@js:
let imgs = [];
let imgArr = result.split('>');
imgArr.forEach(v => {
    // 存在 data-src 则 src 不是真的内容
    const dataMatch = /data-src=\"(\S+)\"/.exec(v)
    if (dataMatch) {
        imgs.push(dataMatch[1])
        return
    }
    // data-src 不存在则看src
    let srcMatch = / src=\"(\S+)\"/.exec(v);
    if (srcMatch) {
        imgs.push(srcMatch[1])
        return
    }
})

imgs.map(v => `<img src="${v}" />`).join("\n")

